import { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

import "./account.css";

export default function Account() {
  const [userproducts, setUserproducts] = useState([]);
  const [buyingorders, setBuyingorders] = useState([]);

  const getSingleUserProducts = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allproductsofuser",
    })
      .then((res) => {
        console.log(res.data.row);
        setUserproducts(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getUserOrders = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/userorders",
    })
      .then((res) => {
        console.log(res.data.row);
        setBuyingorders(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const deleteProduct = (id) => {
    console.log("delete cliked with product id: " + id);
    axios({
      method: "DELETE",
      data: {
        id,
      },
      withCredentials: true,
      url: "http://localhost:4000/api/deleteproduct",
    })
      .then((res) => {
        console.log(res.data);
        // setBuyingorders(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getSingleUserProducts();
    getUserOrders();
  }, []);
  const [tab, setTab] = useState("buying");
  return (
    <>
      <div className="page">
        <h1 className="heading">Account</h1>
        <div className="accountDetails">
          <p>
            <b>Name: </b>Badr Afridi
          </p>
          <p>
            <b>Account status: </b>Buyer and Seller
          </p>
        </div>
        <div className="tab">
          <div
            className="tabs"
            onClick={() => {
              setTab("buying");
            }}
          >
            <h4>Buying</h4>
          </div>
          <div
            className="tabs"
            onClick={() => {
              setTab("selling");
            }}
          >
            <h4>Selling</h4>
          </div>
        </div>
        {tab == "buying" ? (
          <>
            <h2 className="heading">Buying Orders</h2>
            <div className="feeBlocks buyingOrders">
              <div className="first">Product</div>
              <div className="second">Price</div>
              <div className="third">Date</div>
              <div className={"fourth "}>Status</div>
            </div>
            {buyingorders.map((x) => {
              return (
                <div className="feeBlocks buyingOrders">
                  <div className="first">{x.name}</div>
                  <div className="second">{x.price}</div>
                  <div className="third">{x.date}</div>
                  <div
                    className={
                      x.status == "completed" ? "fourth green" : "fourth red"
                    }
                  >
                    {x.status}
                  </div>
                </div>
              );
            })}
          </>
        ) : (
          <>
            <div className="flexRow twoColumns">
              <h2 className="heading">My Products</h2>
              <Link to="/add-new-product">
                <button>Add new Product</button>
              </Link>
            </div>

            {userproducts.map((product) => {
              return (
                <>
                  <div className="feeBlocks" id={product.id}>
                    <div className="first fifty">
                      <img
                        className="accountProductImage"
                        src={"http://localhost:4000/images/" + product.url}
                      ></img>
                      <h4>{product.name}</h4>
                    </div>
                    <div className="second">{product.price}$</div>
                    <div className="third">{product.type}</div>
                    <div className="fourth icons">
                      <i
                        className="fa fa-trash"
                        aria-hidden="true"
                        onClick={() => {
                          deleteProduct(product.id);
                        }}
                      ></i>
                      <i className="fas fa-edit"></i>
                    </div>
                  </div>
                </>
              );
            })}

            <div className="flexRow twoColumns">
              <h2 className="heading">Selling Orders</h2>
            </div>
            <div className="feeBlocks">
              <div className="first fifty">
                <img
                  className="accountProductImage"
                  src={require("../../images/iphone3.webp")}
                ></img>
                <h4>Iphone</h4>
              </div>
              <div className="second">500$</div>
              <div className="third">Direct</div>
              <div className="fourth red">Pending</div>
            </div>
            <div className="feeBlocks">
              <div className="first fifty">
                <img
                  className="accountProductImage"
                  src={require("../../images/iphone3.webp")}
                ></img>
                <h4>Iphone</h4>
              </div>
              <div className="second">400$</div>
              <div className="third">Auction</div>
              <div className="fourth green">Completed</div>
            </div>
          </>
        )}
      </div>
    </>
  );
}
