import { useState, useEffect, useContext } from "react";

import "./account.css";

export default function Account() {
  const [tab, setTab] = useState("buying");
  return (
    <>
      <div className="page">
        <h1 className="heading">Account</h1>
        <div className="accountDetails">
          <p>
            <b>Name: </b>Badr Afridi
          </p>
          <p>
            <b>Account status: </b>Buyer and Seller
          </p>
        </div>
        <div className="tab">
          <div
            className="tabs"
            onClick={() => {
              setTab("buying");
            }}
          >
            <h4>Buying</h4>
          </div>
          <div
            className="tabs"
            onClick={() => {
              setTab("selling");
            }}
          >
            <h4>Selling</h4>
          </div>
        </div>
        {tab == "buying" ? (
          <p>this is buying block</p>
        ) : (
          <p>this is selling block</p>
        )}
      </div>
    </>
  );
}
