import { UserContext } from "./userContext";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { useEffect, useState, useMemo } from "react";
import { Link } from "react-router-dom";
import Topbar from "./components/topbar/Topbar";
import Login from "./pages/login/Login";
import Home from "./pages/home/Home";
import Footer from "./components/footer/Footer";
import Products from "./pages/products/Products";
import Single_product from "./pages/single_product/Single_product";
import Categories from "./pages/categories/Categories";
import Single_category from "./pages/single_category/Single_category";
import Cart from "./pages/cart/Cart";
import Account from "./pages/account/Account";

function App() {
  const [usertop, setUsertop] = useState(null);

  const providerValue = useMemo(
    () => ({ usertop, setUsertop }),
    [usertop, setUsertop]
  );

  // getting user data like name etc
  const getUser = () => {
    fetch("http://localhost:4000/api/user", {
      method: "GET",
      credentials: "include",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "Access-Control-Allow-Credentials": true,
      },
    })
      .then((res) => res.json())
      .then((res) => {
        if (res.user) {
          setUsertop(res.user[0]);
        } else {
          console.log("user not found");
        }
      })

      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    getUser();
  }, []);

  return (
    <UserContext.Provider value={providerValue}>
      <Router>
        {usertop ? <Topbar usertop={usertop} /> : <></>}
        <Routes>
          {/* -------- admin routes ---------- */}
          <Route
            path="/login"
            element={usertop ? <Navigate to="/" /> : <Login />}
          />

          <Route
            path="/"
            element={usertop ? <Home /> : <Navigate to="/login" />}
          />

          <Route path="/products" element={<Products />} />
          <Route path="/product/:productId" element={<Single_product />} />

          <Route path="/categories" element={<Categories />} />
          <Route path="/category/:categoryId" element={<Single_category />} />

          <Route path="/cart" element={<Cart />} />
          <Route path="/account" element={<Account />} />
        </Routes>

        {usertop ? <Footer /> : <></>}
      </Router>
    </UserContext.Provider>
  );
}

export default App;
