import "./new_product.css";
import { useState, useEffect, useRef } from "react";
import axios from "axios";
import swal from "sweetalert";

export default function New_product() {
  const name = useRef();
  const price = useRef();
  const type = useRef();
  const category = useRef();
  const [url, setUrl] = useState("");
  const [file, setFile] = useState(null);

  const submitHandler = async (e) => {
    e.preventDefault();
    const namex = name.current.value;
    const pricex = price.current.value;
    const typex = type.current.value;
    const categoryx = category.current.value;
    const urlx = "";

    if (file) {
      const data = new FormData();
      const fileName = Date.now() + file.name;
      data.append("file", file);
      data.append("name", fileName);
      urlx = fileName;

      try {
        await axios.post("/upload", data);
      } catch (err) {
        console.log(err);
      }
    }

    axios({
      method: "POST",
      data: {
        namex,
        pricex,
        typex,
        categoryx,
        urlx,
      },
      withCredentials: true,
      url: "http://localhost:4000/api/addnewproduct",
    })
      .then((res) => {
        swal({
          title: "Product added successfully",
          text: "You can see all your products on account page",
          icon: "success",
          button: "Ok",
        });
        //
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <div className="page">
        <form
          onSubmit={(e) => {
            submitHandler(e);
          }}
        >
          <div className="formItem">
            <label>Product name</label>
            <input type="text" ref={name}></input>
          </div>
          <div className="formItem">
            <label>Price</label>
            <input type="number" ref={price}></input>
          </div>
          <div className="formItem">
            <label>Selling type</label>
            <input type="text" ref={type}></input>
          </div>
          <div className="formItem">
            <label>Product image</label>
            <input
              type="file"
              accept=".png,.jpg,.jpeg,"
              onChange={(e) => {
                setFile(e.target.files[0]);
              }}
            ></input>
          </div>
          <div className="formItem">
            <label>Category</label>
            <input type="text" ref={category}></input>
          </div>
          <div className="formItem">
            <input type="submit"></input>
          </div>
        </form>
      </div>
    </>
  );
}
