import { useState, useEffect, useContext } from "react";
import "./cart.css";
import { CartContext } from "../../cartContext";

export default function Cart() {
  const [products, setProducts] = useState([]);
  const { cart, setCart } = useContext(CartContext);

  const getProducts = () => {
    axios({
      method: "POST",
      data: { cart },
      withCredentials: true,
      url: "http://localhost:4000/api/getproductsforcart/",
    })
      .then((res) => {
        setProduct(res.data.row[0]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <>
      <div className="page">
        <h1 className="heading">Cart</h1>
        <div className="cart">
          <table className="table">
            <thead>
              <tr className="links">
                <th scope="col">Product</th>
                <th scope="col">Quantity</th>
                <th scope="col">Price</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">Iphone</th>
                <td scope="row">1</td>
                <td scope="row">500$</td>
              </tr>
            </tbody>
          </table>
        </div>
        <h1 className="heading">Checkout</h1>
        <form
        // onSubmit={(e) => {
        //   addAttendence(e);
        // }}
        >
          <div className="form-outline mb-4">
            <input
              required
              type="text"
              id="form2Example1"
              className="form-control"
              placeholder="Name"
              // onChange={(e) => {
              //   setDate(e.target.value);
              // }}
            />
          </div>
          <div className="form-outline mb-4">
            <input
              required
              type="text"
              id="form2Example1"
              className="form-control"
              placeholder="Phone"
              // onChange={(e) => {
              //   setDate(e.target.value);
              // }}
            />
          </div>

          <div className="form-outline mb-4">
            <input
              required
              type="text"
              id="form2Example1"
              className="form-control"
              placeholder="Address"
              // onChange={(e) => {
              //   setDate(e.target.value);
              // }}
            />
          </div>
          <p>
            <b>Payment Method: </b>Cash on delivery
          </p>
          <p>
            <b>Total: </b>500$
          </p>
          <div className="row mb-4">
            <div className="col">
              {" "}
              <button
                type="submit"
                className="btn btn-primary btn-block mb-4"
                style={{ width: 100 + "%" }}
              >
                Place Order
              </button>
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
