import { useState, useEffect, useContext } from "react";
import "./single_product.css";
import axios from "axios";
import { useLocation } from "react-router-dom";

export default function Single_product() {
  const [product, setProduct] = useState([]);
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const getProduct = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allsingleproduct/" + id,
    })
      .then((res) => {
        console.log(res.data.row[0]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getProduct();
  }, []);
  return (
    <>
      <div className="page">
        <div className="flexRow twoColumns">
          <div className="flexColumn full productPageProductImageBlock">
            <img src={require("../../images/iphone3.webp")}></img>
          </div>
          <div className="flexColumn full productPageProductDetailsBlock">
            <p style={{ fontSize: "14px" }}>
              <b>Category: </b>Phones
            </p>
            <h3>IPhone X 1TB</h3>
            <h3>499$</h3>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce
              rutrum vehicula elit, id mattis est sodales id. Sed lacinia
              tincidunt metus et egestas. Aenean non elit dictum, egestas mauris
              a, euismod enim. Donec euismod lobortis vulputate. Phasellus ac
              lacus rhoncus, ultrices orci sit amet, efficitur dolor.
              Pellentesque eget commodo turpis. Proin sit amet molestie diam.
              Donec ut massa sit amet mi scelerisque interdum a nec lectus. Sed
              quam urna, sagittis dictum bibendum nec, mattis id nibh.
            </p>
            <p>
              <b>Buying Method: </b>auction
            </p>
            <p>
              <b>Last Bid: </b>300$
            </p>
            <p>
              <b>Auction End Date: </b>30/5/2020
            </p>
            <input type="text" placeholder="Your Bid"></input>
            <input type="submit" value="Place Bid"></input>
          </div>
        </div>
        <div className="flexRow twoColumns"></div>
        <div className="flexRow twoColumns"></div>
      </div>
    </>
  );
}
