import { useState, useEffect, useContext } from "react";
import "./single_product.css";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { UserContext } from "../../userContext";
import { CartContext } from "../../cartContext";
import swal from "sweetalert";

export default function Single_product() {
  const { usertop, setUsertop } = useContext(UserContext);
  const { cart, setCart } = useContext(CartContext);
  const [product, setProduct] = useState([]);
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const getProduct = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allsingleproduct/" + id,
    })
      .then((res) => {
        setProduct(res.data.row[0]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addtocart = () => {
    console.log(typeof cart);
    if (cart != null) {
      var alreadyincart = cart.includes(product.id);
      if (alreadyincart) {
        console.log("can't add, product already in cart.");
      } else {
        arr = [...cart];
        arr.push(product.id);
        setCart(arr);
      }
    } else {
      var arr = [product.id];
      setCart(arr);
    }
  };

  useEffect(() => {
    getProduct();
  }, []);
  return (
    <>
      <div className="page">
        <div className="flexRow twoColumns">
          <div className="flexColumn full productPageProductImageBlock">
            <img
              src={
                product.url ? "http://localhost:4000/images/" + product.url : ""
              }
            ></img>
          </div>
          <div className="flexColumn full productPageProductDetailsBlock">
            <p style={{ fontSize: "14px" }}>
              <b>Category: </b>
              <Link to={"/category/" + product.category}>
                {product.category}
              </Link>
            </p>
            <h3 style={{ fontSize: "34px" }}>{product.name}</h3>
            <h3 style={{ fontSize: "21px" }}>Price: {product.price}</h3>
            <p>{product.description}</p>
            <p>
              <b>Buying Method: </b>
              {product.type}
            </p>
            {product.type == "direct" ? (
              <>
                <input
                  type="submit"
                  value="Add to Cart"
                  onClick={(e) => {
                    addtocart(e);
                  }}
                ></input>
              </>
            ) : (
              <></>
            )}
            {/* <p>
              <b>Last Bid: </b>
              <p className="red">300$ to be done</p>
            </p>
            <p>
              <b>Auction End Date: </b>30/5/2020{" "}
              <p className="red">to be done</p>
            </p> 
                        <input type="text" placeholder="Your Bid"></input>
            <input type="submit" value="Place Bid"></input>
            */}
          </div>
        </div>
        <div className="flexRow twoColumns"></div>
        <div className="flexRow twoColumns"></div>
      </div>
    </>
  );
}
