import { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import axios from "axios";

export default function Single_category() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [products, setProducts] = useState([]);

  const getProducts = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/getproductsofsinglecategory/" + path,
    })
      .then((res) => {
        setProducts(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getProducts();
  }, []);
  return (
    <>
      <div className="page">
        <h1 className="heading">Category name : IPhone</h1>
        <div className="homeProductsBlock">
          {products.length > 0 ? (
            products.map((product) => {
              return (
                <>
                  <div className="homeProductsSingle">
                    <Link to={"/product/" + product.id}>
                      {" "}
                      <img
                        className="productImage"
                        src={"http://localhost:4000/images/" + product.url}
                      ></img>
                      <h4 className="productTitle">{product.name}</h4>
                    </Link>
                  </div>
                </>
              );
            })
          ) : (
            <>
              <p>No products found in this category, try other categories</p>
            </>
          )}
        </div>
      </div>
    </>
  );
}
