import { useLocation } from "react-router-dom";
import { useState, useEffect, useContext } from "react";
import axios from "axios";
import { UserContext } from "../../userContext";
import { CartContext } from "../../cartContext";

export default function Single_order() {
    const location = useLocation();
    const id = location.pathname.split("/")[2];
    const [orderdetails, setOrderdetails] = useState([])

    const getSingleorder = () => {
        axios({
            method: "GET",
            withCredentials: true,
            url: "http://localhost:4000/api/getproductsforcart/" + id,
        })
            .then((res) => {

                setOrderdetails(res.data.row[0])
                console.log(res.data.row[0])
            })
            .catch((error) => {
                console.log(error);
            });
    }

    const markDelivered = (order_id) => {

        axios({
            method: "POST",
            data: {
                order_id,
            },
            withCredentials: true,
            url: "http://localhost:4000/api/markasdelivered",
        })
            .then((res) => {

                setOrderdetails(res.data.row[0])
                console.log(res.data.row[0])
            })
            .catch((error) => {
                console.log(error);
            });

    }

    useEffect(() => {
        getSingleorder();
        
    }, [])
    return (
        <>
            <div className="page">
                <p><b>Buyer name: </b>{orderdetails.buyer_name}</p>
                <p><b>Buyer Phone: </b>{orderdetails.buyer_phone}</p>
                <p><b>Buyer Address: </b>{orderdetails.buyer_address}</p>
                <p><b>Status: </b>{orderdetails.status}</p>
                <p><b>Date: </b>{orderdetails.date}</p>
                {orderdetails.status == "pending" && <p className="red">Please deliver this order as soon as possible.</p>}

                <button onClick={()=>{
                    markDelivered(orderdetails.id);
                }} className="btn btn-primary btn-block mb-4">Mark as delivered</button>

                
            </div>
        </>
    )
}
