import "./home.css";
import { useState } from "react";
import axios from "axios";

export default function Home() {
  const [state, setState] = useState("login");
  const [login_username, setLogin_username] = useState("");
  const [login_password, setLogin_password] = useState("");
  const [register_username, setRegister_username] = useState("");
  const [register_password, setRegister_password] = useState("");
  const [register_phone, setRegister_phone] = useState("");
  const [register_email, setRegister_email] = useState("");

  const registerSubmit = (e) => {
    e.preventDefault();
    console.log("axios running");
    axios({
      method: "POST",
      data: {
        register_username,
        register_password,
        register_phone,
        register_email,
      },
      withCredentials: true,
      url: "http://localhost:4000/api/register",
    })
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        // if (error.response.status == 401) {
        //   setError("Invalid credentials, please try again");
        // }
        console.log(error);
      });
  };

  if (state == "login")
    return (
      <>
        <div className="loginForm">
          <form>
            <div className="form-outline mb-4">
              <h2>Login</h2>
            </div>
            <div className="form-outline mb-4">
              <input
                type="text"
                id="form2Example1"
                className="form-control"
                placeholder="Username"
              />
            </div>
            <div className="form-outline mb-4">
              <input
                type="password"
                id="form2Example2"
                className="form-control"
                placeholder="Password"
              />
            </div>
            <div className="row mb-4">
              <div className="col">
                {" "}
                <button
                  type="button"
                  className="btn btn-primary btn-block mb-4"
                  style={{ width: 100 + "%" }}
                >
                  Sign in
                </button>
              </div>
            </div>
            <div className="row mb-4">
              <div className="col">
                <a href="#!">Forgot password?</a>
              </div>
            </div>
            <div className="text-center">
              <p>
                Not a member?{" "}
                <a
                  href="#!"
                  onClick={() => {
                    setState("register");
                  }}
                >
                  Register
                </a>
              </p>
            </div>
          </form>
        </div>
      </>
    );
  if (state == "register")
    return (
      <>
        <div className="loginForm">
          <form
            onSubmit={(e) => {
              registerSubmit(e);
            }}
          >
            <div className="form-outline mb-4">
              <h2>Register</h2>
            </div>
            <div className="form-outline mb-4">
              <input
                type="text"
                id="form2Example1"
                className="form-control"
                placeholder="Username"
                onChange={(e) => {
                  setRegister_username(e.target.value);
                }}
              />
            </div>
            <div className="form-outline mb-4">
              <input
                type="number"
                id="form2Example1"
                className="form-control"
                placeholder="Phone"
                onChange={(e) => {
                  setRegister_phone(e.target.value);
                }}
              />
            </div>
            <div className="form-outline mb-4">
              <input
                type="Email"
                id="form2Example1"
                className="form-control"
                placeholder="Email"
                onChange={(e) => {
                  setRegister_email(e.target.value);
                }}
              />
            </div>

            <div className="form-outline mb-4">
              <input
                type="password"
                id="form2Example2"
                className="form-control"
                placeholder="Password"
                onChange={(e) => {
                  setRegister_password(e.target.value);
                }}
              />
            </div>

            <div className="row mb-4">
              <div className="col">
                {" "}
                <button
                  type="submit"
                  className="btn btn-primary btn-block mb-4"
                  style={{ width: 100 + "%" }}
                >
                  Sign up
                </button>
              </div>
            </div>

            <div className="text-center">
              <p>
                Already a member?{" "}
                <a
                  href="#!"
                  onClick={() => {
                    setState("login");
                  }}
                >
                  Sign in
                </a>
              </p>
            </div>
          </form>
        </div>
      </>
    );
}
