import "./topbar.css";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { UserContext } from "../../userContext";
import { useNavigate } from "react-router-dom";
import logo from "../../images/logo.png";

export default function Topbar() {
  const { usertop, setUsertop } = useContext(UserContext);
  const navigate = useNavigate();
  const logout = () => {
    axios({
      method: "POST",
      withCredentials: true,
      url: "http://localhost:4000/api/logout",
    })
      .then((res) => {
        if (res.status == 200) {
          setUsertop(null);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  // topbar for admin
  console.log(usertop.status);

  return (
    <>
      <div className="topbar">
        <div className="topLeft">
          {/* <i className="fa-brands fa-facebook-square topIconSocial"></i>
        <i className="fa-brands fa-twitter-square topIconSocial"></i>
        <i className="fa-brands fa-instagram-square topIconSocial"></i> */}
          <img width={150} src={logo} />
          {/* {usertop.status == "admin" && <h2>Admin Portal</h2>}
        {usertop.status == "teacher" && <h2>Teacher Portal</h2>}
        {usertop.status == "student" && <h2>Student Portal</h2>} */}
        </div>
        <div className="topMiddle">
          <ul className="topList">
            <li className="topListItem">
              <Link to="/">home</Link>
            </li>
            <li className="topListItem">
              <Link to="/products">products</Link>
            </li>
            <li className="topListItem">
              <Link to="/categories">categories</Link>
            </li>
            {usertop.status == "teacher" && (
              <>
                <li className="topListItem">
                  <Link to="/teacher/profile">PROFILE</Link>
                </li>
                <li className="topListItem">
                  <Link to="/teacher/marks">MARKS</Link>
                </li>
                <li className="topListItem">
                  <Link to="/teacher/classes">CLASSES</Link>
                </li>
                <li className="topListItem">
                  <Link to="/teacher/notes">NOTES</Link>
                </li>
                <li className="topListItem">
                  <Link to="/teacher/attendance">ATTENDANCE</Link>
                </li>
              </>
            )}
            {usertop ? (
              <li
                className="topListItem"
                onClick={() => {
                  logout();
                }}
              >
                logout
              </li>
            ) : (
              <></>
            )}
          </ul>
        </div>
        <div className="topRight">
          {usertop && <p>welcome {usertop.username}</p>}

          <i className="fa-solid fa-magnifying-glass topIconRight"></i>
        </div>
      </div>
      <div id="topCategoriesBlock">
        <ul id="topCategoriesUl">
          <li>
            <a href="#">Phones</a>
          </li>
          <li>
            <a href="#">Houses</a>
          </li>
          <li>
            <a href="#">Watches</a>
          </li>
          <li>
            <a href="#">Glasses</a>
          </li>
          <li>
            <a href="#">Games</a>
          </li>
          <li>
            <a href="#">Hardware</a>
          </li>
        </ul>
      </div>
    </>
  );
}
