import { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

import "./account.css";

export default function Account() {
  const [userproducts, setUserproducts] = useState([]);
  const [buyingorders, setBuyingorders] = useState([]);
  const [sellingorders, setSellingorders] = useState([]);

  const getSingleUserProducts = () => {
    console.log("getting products function called from account apge");
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allproductsofuser",
    })
      .then((res) => {
        console.log(res.data.row);
        setUserproducts(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getUserOrders = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/userorders",
    })
      .then((res) => {
        console.log(res);
        console.log(res.data.row);
        setBuyingorders(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const deleteProduct = (id) => {
    console.log("delete cliked with product id: " + id);
    axios({
      method: "DELETE",
      data: {
        id,
      },
      withCredentials: true,
      url: "http://localhost:4000/api/deleteproduct",
    })
      .then((res) => {
        console.log(res.data);
        // setBuyingorders(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getSellingorders = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/usersellingorders",
    })
      .then((res) => {
        console.log('here are the selling orderes')
        console.log(res);
        console.log(res.data.row);
        // setBuyingorders(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };


  useEffect(() => {
    getSingleUserProducts();
    getUserOrders();
    getSellingorders();
  }, []);
  const [tab, setTab] = useState("buying");
  return (
    <>
      <div className="page">
        <h1 className="heading">Account</h1>
        <div className="accountDetails">
          <p>
            <b>Name: </b>Badr Afridi
          </p>
          <p>
            <b>Account status: </b>Buyer and Seller
          </p>
        </div>
        <div className="tab">
          <div
            className="tabs"
            onClick={() => {
              setTab("buying");
            }}
          >
            <h4>Buying</h4>
          </div>
          <div
            className="tabs"
            onClick={() => {
              setTab("selling");
            }}
          >
            <h4>Selling</h4>
          </div>
        </div>
        {tab == "buying" ? (
          <>
            <h2 className="heading">Buying Orders</h2>
            <div className="feeBlocks buyingOrders tableHead">
              <div className="first">Product</div>
              <div className="second">Price</div>
              <div className="third">Date</div>
              <div className={"fourth "}>Status</div>
            </div>
            {buyingorders &&
              buyingorders.map((x) => {
                return (
                  <div className="feeBlocks buyingOrders">
                    <div className="first">{x.name}</div>
                    <div className="second">{x.price}</div>
                    <div className="third">{x.date}</div>
                    <div
                      className={
                        x.status == "completed" ? "fourth green" : "fourth red"
                      }
                    >
                      {x.status}
                    </div>
                  </div>
                );
              })}
          </>
        ) : (
          <>
            <div className="flexRow twoColumns">
              <h2 className="heading">My Products</h2>
              <Link to="/add-new-product">
                <button>Add new Product</button>
              </Link>
            </div>

            <div className="feeBlocks tableHead">
              <div className="first fifty">Product</div>
              <div className="second">Price</div>
              <div className="third">Type</div>
              <div className="fourth icons">Actions</div>
            </div>
            {userproducts &&
              userproducts.map((product) => {
                return (
                  <>
                    <div className="feeBlocks" id={product.id}>
                      <div className="first fifty">
                        <img
                          className="accountProductImage"
                          src={"http://localhost:4000/images/" + product.url}
                        ></img>
                        <h4>{product.name}</h4>
                      </div>
                      <div className="second">{product.price}$</div>
                      <div className="third">{product.type}</div>
                      <div className="fourth icons">
                        <i
                          className="fa fa-trash"
                          aria-hidden="true"
                          onClick={() => {
                            deleteProduct(product.id);
                          }}
                        ></i>
                        <i className="fas fa-edit"></i>
                      </div>
                    </div>
                  </>
                );
              })}

            <div className="flexRow twoColumns">
              <h2 className="heading">Selling Orders</h2>
            </div>
            <div className="feeBlocks tableHead sellingOrders">
              <div className="first fifty">
                <img
                  className="accountProductImage"
                ></img>
                <h4>Product</h4>
              </div>
              <div className="second">Price</div>
              <div className="third">By</div>
              <div className="fourth ">Type</div>
              <div className="fifth red">Status</div>

            </div>
            <Link to="/order/1" className="feeBlocks sellingOrders">
              <div className="first fifty">
                <img
                  className="accountProductImage"
                  // src={require("../../images/iphone3.webp")}
                ></img>
                <h4>Iphone</h4>
              </div>
              <div className="second">500$</div>
              <div className="third">Imran</div>
              <div className="fourth ">Direct</div>
              <div className="fifth red">Pending</div>
            </Link>
            <Link to="/order/1" className="feeBlocks sellingOrders">
              <div className="first fifty">
                <img
                  className="accountProductImage"
                  // src={require("../../images/iphone3.webp")}
                ></img>
                <h4>Iphone</h4>
              </div>
              <div className="second">400$</div>
              <div className="third">Safwan</div>
              <div className="fourth ">Auction</div>
              <div className="fifth red">Pending</div>
            </Link>
          </>
        )}
      </div>
    </>
  );
}
