import { useState, useEffect, useContext, useRef } from "react";
import "./single_product.css";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { UserContext } from "../../userContext";
import { CartContext } from "../../cartContext";
import swal from "sweetalert";

export default function Single_product() {
  const { usertop, setUsertop } = useContext(UserContext);
  const { cart, setCart } = useContext(CartContext);
  const [product, setProduct] = useState([]);
  const location = useLocation();
  const id = location.pathname.split("/")[2];
  const [error, setError] = useState();
  const [timepassed, setTimepassed] = useState(false);
  const bid = useRef(0);

  const getProduct = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allsingleproduct/" + id,
    })
      .then((res) => {
        setProduct(res.data.row[0]);
        if (res.data.row[0].type == "auction") {
          var x = res.data.row[0].end_date;
          if (new Date(x).toLocaleString() < new Date().toLocaleString()) {
            console.log("auction time has passed");
            setTimepassed(true);
          }
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const placeBid = () => {
    const bidx = bid.current.value;
    const product_id = product.id;
    axios({
      method: "PUT",
      withCredentials: true,
      data: { bidx, product_id },
      url: "http://localhost:4000/api/placebid/",
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addtocart = () => {
    console.log(typeof cart);
    if (cart != null) {
      var alreadyincart = cart.includes(product.id);
      if (alreadyincart) {
        setError("cannot add product to cart. product already in cart");
      } else {
        arr = [...cart];
        arr.push(product.id);
        setCart(arr);
        setError("");
        swal({
          title: "Product added to cart successfully",
          text: "you can see this product in your cart",
          icon: "success",
          button: "Ok",
        });
      }
    } else {
      var arr = [product.id];
      setCart(arr);
      swal({
        title: "Product added to cart successfully",
        text: "you can see this product in your cart",
        icon: "success",
        button: "Ok",
      });
    }
  };

  useEffect(() => {
    getProduct();
  }, []);
  return (
    <>
      <div className="page">
        <div className="flexRow twoColumns">
          <div className="flexColumn full productPageProductImageBlock">
            <img
              src={
                product.url ? "http://localhost:4000/images/" + product.url : ""
              }
            ></img>
          </div>
          <div className="flexColumn full productPageProductDetailsBlock">
            <p style={{ fontSize: "14px" }}>
              <b>Category: </b>
              <Link to={"/category/" + product.category}>
                {product.category}
              </Link>
            </p>
            <h3 style={{ fontSize: "34px" }}>{product.name}</h3>
            <h3 style={{ fontSize: "21px" }}>Price: {product.price}</h3>
            <p>{product.description}</p>
            <p>
              <b>Buying Method: </b>
              {product.type}
            </p>
            {product.type == "direct" ? (
              <>
                {!usertop ? (
                  <>Please login to add product to the cart</>
                ) : usertop.id == product.user_id ? (
                  <>You can not place an order on your own product</>
                ) : (
                  <input
                    type="submit"
                    value="Add to Cart"
                    className="btn btn-primary btn-block mb-4"
                    onClick={(e) => {
                      addtocart(e);
                    }}
                  ></input>
                )}

                <p
                  className="error"
                  style={{ color: "red", marginTop: "10px" }}
                >
                  {error}
                </p>
              </>
            ) : (
              <>
                {timepassed ? (
                  <>
                    <p className="red">
                      Sorry, auction time has ended on{" "}
                      {new Date(product.end_date).toLocaleString()}
                    </p>
                  </>
                ) : (
                  <>
                    <b>Last bid: </b>
                    {product.last_bid}
                    <br />
                    <b>Bid ending time: </b>
                    {new Date(product.end_date).toLocaleString()}
                    <br />
                    <b>Last bidder name: </b>Salman Khan
                    <br />
                    <div
                      className="form-outline mb-4"
                      style={{ marginTop: "30px" }}
                    >
                      <input
                        type="number"
                        placeholder="Your Bid"
                        className="form-control"
                        ref={bid}
                      ></input>
                    </div>
                    <div className="mb-4">
                      <input
                        style={{ width: "100%" }}
                        type="submit"
                        value="Place Bid"
                        className="btn btn-primary btn-block mb-4"
                        onClick={() => {
                          placeBid();
                        }}
                      ></input>
                    </div>
                  </>
                )}
              </>
            )}
          </div>
        </div>
        <div className="flexRow twoColumns"></div>
        <div className="flexRow twoColumns"></div>
      </div>
    </>
  );
}
