import { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Products() {
  const [allproducts, setAllproducts] = useState([]);

  const allProducts = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:4000/api/allproducts",
    })
      .then((res) => {
        console.log(res);
        setAllproducts(res.data.row);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    allProducts();
  }, []);
  return (
    <>
      <div className="page">
        <h1 className="heading">Products</h1>
        <div className="homeProductsBlock">
          {allproducts.map((product) => {
            return (
              <div className="homeProductsSingle" key={product.id}>
                <Link to={"/product/" + product.id}>
                  {" "}
                  <img
                    className="productImage"
                    src={"http://localhost:4000/images/" + product.url}
                  ></img>
                  <h4 className="productTitle">{product.name}</h4>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}
